require 'test_helper'
require 'vendor/plugins/pdf-stamper/lib/pdf/fillpdf.rb'
require 'base64'
require 'xmlrpc/client'

class FillpdfControllerTest < ActionController::TestCase
  def setup
    @pdf_bytes = File.read 'vendor/plugins/pdf-stamper/lib/pdf/sample.pdf'
    @xfdf_bytes = File.read 'vendor/plugins/pdf-stamper/lib/pdf/sample.xfdf'
    @server = XMLRPC::Client.new2("http://localhost:3000/fillpdf")
  end
  
  test "xmlrpc works" do
    result = @server.call("parse_pdf", Base64.encode64(@pdf_bytes), '')
    assert_no_match(/faultCode/, result) if result.is_a? String

    result = @server.call("merge_pdf", @pdf_bytes, @xfdf_bytes, '')
    assert_no_match(/faultCode/, result) if result.is_a? String
  end
  
  test "parse pdf from file" do
    pdf = PDF::Fillpdf.new('vendor/plugins/pdf-stamper/lib/pdf/sample.pdf', {'from'=>'file'})
    arr = pdf.parse
    assert_equal arr, [{"name"=>"Date of Birth", "value"=>"", "type"=>"Text"}, {"name"=>"Your Name", "value"=>"", "type"=>"Text"}]
  end
  
  test "parse_as_xfdf from bytes" do
    pdf = PDF::Fillpdf.new(@pdf_bytes, {'from'=>'bytes'})
    xfdf = pdf.parse_as_xfdf
    assert_equal xfdf, "<fields>\n<field name='Date of Birth' type='Text' value=''/>\n<field name='Your Name' type='Text' value=''/></fields>"
  end
  
  
